package challenge;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for (int i=0; i<10; i++) {
	System.out.println(i);
}
for (int i=10; i>=0; i--) {
	System.out.println(i);
} for (int i=0; i<=100; i += 2) {
	if (i % 2 == 0) {
	System.out.println(i);
}
	}

}}
