package challenge;

public class Oddeven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for (int i=0; i<=100; i++) {
	if (i %2 ==0) {
		System.out.println(i +" even");
	}else
		System.out.println(i +" odd");
} int x=100;
while (x>=0) {
	System.out.println(x + " " + x % 3);
	x--;
}
	}

}
